import React from 'react'
function Select() {

  // const getValue = (ele) => {
  //     ele.target.value; 
  //   };
  // console.log(getValue);

  const handleChange = (e) => {
    e.target.value
  }

  return (
    <select  >
        <option onChange={handleChange} value="none" disabled>Move to...</option>
        <option onChange={ handleChange(e)} value="currentlyReading">Currently Reading</option>
        <option onChange={ handleChange(e)} value="wantToRead">Want to Read</option>
        <option onChange={ handleChange(e)} value="read">Read</option>
        <option onChange={ handleChange(e)} value="none">None</option>
    </select>
  )
}

export default Select
