import React from 'react'
function Select() {
  const getValue = () => { return this.props.value; };
  return (
    <select>
        <option onClick={getValue} value="none" disabled>Move to...</option>
        <option onClick={getValue} value="currentlyReading">Currently Reading</option>
        <option onClick={getValue} value="wantToRead">Want to Read</option>
        <option onClick={getValue} value="read">Read</option>
        <option onClick={getValue} value="none">None</option>
    </select>
  )
}

export default Select
