import "./App.css";
import { useEffect, useState } from "react";
import Header from "./components/Header";
import Bookshelf from "./components/Bookshelf";
import * as BookAPI from "./BooksAPI";

/**
 * #When?
 * - when selecting a shelf
 *
 * #How?
 * MoveBook
 * 1- take book from one shelf (delete)
 * 2- move it to another shelf (append/push)
 * ---------------------
 * variables:
 * - book (?)
 * - current shelf
 * - new shelf
 * --------------------
 * code:
 * 1- read variables [/]
 * 1- push in the new shelf
 *
 *
 * --------------------
 * setup
 *   const changeShelf = () => {};
 */


function App() {
  // read state => variable
  const ar = [
    { name: "ahmed", array: [5, 6, 8, 9] },
    { name: "mhmd", array: [5, 6, 8, 9] },
    { name: "eid", array: [5, 6, 8, 9] },
    { name: "ali", array: [5, 6, 8, 9] },
  ]; // replace with state
// console.log("before")
// console.log(ar)
setTimeout(()=>{

  console.log(ar.find((item)=>{return (item.name === 'mhmd')}).array.push(10))
  // console.log("after")
  // console.log(ar)
},3000)
// set state => variable => ar

  
  const [bookshelf, setbookshelf] = useState([]);
  const [showSearchPage, setShowSearchpage] = useState(false);
  const [shelfArr, setshelfArr] = useState([]);

  const changeShelf = (data) => {
    //shelfArr
    const currentShelfArr = shelfArr;
    currentShelfArr.find(()=>{})
    // all the logic
      data.find((data)=>{return (data.bookId)})
    // data.find((book)=>{return (book.shelf === 'currentbookShelf')}).array.push(book);
    // select.toLowerCase().replace(/\s/g, '') ?== item.toLowerCase().replace(/\s/g, '')
    /**
     *{  bookId,   currentbookShelf,   newBookShelf}*/
  };

  // useEffect
  useEffect(() => {
    // run something in the lifecycle
    BookAPI.getAll().then((books) => {
      // books
      // inside this => access books
      setbookshelf(books);
      // console.log(books[0].shelf);
      bookArrRefactor(books);
    });
  }, []);

  const bookArrRefactor = (booksArray) => {
    const array1 = [];
    const array2 = [];
    const array3 = [];

    booksArray.forEach(dist);
    function dist(item) {
      const shelf = item.shelf;
      // console.log(shelf);
      if (shelf === "currentlyReading") {
        array1.push(item);
      } else if (shelf === "wantToRead") {
        array2.push(item);
      } else {
        array3.push(item);
      }
    }
    const newShelfArr = [
      { title: "Currently Reading", array: array1 },
      { title: "Want to Read", array: array2 },
      { title: "Read", array: array3 },
    ];
    setshelfArr(newShelfArr);
  };

  return (
    <div className="app">
      {showSearchPage ? (
        <div className="search-books">
          <div className="search-books-bar">
            <a
              className="close-search"
              onClick={() => setShowSearchpage(!showSearchPage)}
            >
              Close
            </a>
            <div className="search-books-input-wrapper">
              <input
                type="text"
                placeholder="Search by title, author, or ISBN"
              />
            </div>
          </div>
          <div className="search-books-results">
            <ol className="books-grid"></ol>
          </div>
        </div>
      ) : (
        <div className="list-books">
          <Header title="My Reads" />
          <div className="list-books-content">
            {shelfArr.length > 0 &&
              shelfArr.map((item, idx) => (
                <div key={`${idx}-${item.title}`}>
                  <Bookshelf
                    title={item.title}
                    bookArr={item.array}
                    onChangeShelf={changeShelf}
                  />
                </div>
              ))}
          </div>
          <div className="open-search">
            <a onClick={() => setShowSearchpage(!showSearchPage)}>Add a book</a>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
