import React from "react";
import Book from "./Book";
import { useEffect, useState } from "react";
const Bookshelf = ({ title ,bookArr,onChangeShelf}) => {

const [bookshelf, setShelf] = useState("");

  const updateBookShelf = (newShelf) => { // newShelf == newSelect
    //3 variable
    //1- book details
    //2- current shelf
    //3- new shelf [/]
    const bookShelf =  newShelf
    setShelf(bookShelf)
    console.log(bookShelf);
  }

  return (
    <div className="bookshelf">
      <h2 className="bookshelf-title">{title}</h2>
      <div className="bookshelf-books">
        <ol className="books-grid">
            {bookArr.map((item,idx)=> <li key={`${idx}-${item.title}`}>
            <Book
              book={item}
              value={bookshelf}
              onChange={updateBookShelf}
            />
          </li>)}
        </ol>
      </div>
    </div>
  );
};
export default Bookshelf;
